import React, { useState, useEffect } from 'react';
import { FaPlus, FaClipboardCheck, FaExclamationTriangle, FaCheckCircle, FaEye, FaEdit, FaTrash, FaCalendarAlt, FaUser, FaBook } from 'react-icons/fa';
import './InventoryChecks.css';

const InventoryChecks = () => {
  const [checks, setChecks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    total: 0,
    pending: 0,
    completed: 0,
    discrepancy: 0
  });

  useEffect(() => {
    // Mock data for inventory checks
    const mockChecks = [
      {
        id: 1,
        checkNumber: 'KK001',
        title: 'Kiểm kê tổng thể tháng 1/2024',
        assignedTo: 'Nguyễn Văn A',
        startDate: '2024-01-15',
        endDate: '2024-01-20',
        status: 'completed',
        progress: 100,
        totalBooks: 1250,
        checkedBooks: 1250,
        discrepancyCount: 5,
        notes: 'Hoàn thành kiểm kê, phát hiện 5 cuốn sách bị hư hỏng nhẹ'
      },
      {
        id: 2,
        checkNumber: 'KK002',
        title: 'Kiểm kê khu vực A - Khoa học',
        assignedTo: 'Trần Thị B',
        startDate: '2024-02-01',
        endDate: '2024-02-05',
        status: 'pending',
        progress: 60,
        totalBooks: 450,
        checkedBooks: 270,
        discrepancyCount: 2,
        notes: 'Đang tiến hành kiểm kê, phát hiện 2 cuốn sách bị mất'
      },
      {
        id: 3,
        checkNumber: 'KK003',
        title: 'Kiểm kê định kỳ quý 1',
        assignedTo: 'Lê Văn C',
        startDate: '2024-03-01',
        endDate: '2024-03-15',
        status: 'discrepancy',
        progress: 85,
        totalBooks: 800,
        checkedBooks: 680,
        discrepancyCount: 12,
        notes: 'Phát hiện nhiều sách bị hư hỏng, cần báo cáo cấp trên'
      },
      {
        id: 4,
        checkNumber: 'KK004',
        title: 'Kiểm kê sách mới nhập',
        assignedTo: 'Phạm Thị D',
        startDate: '2024-02-20',
        endDate: '2024-02-25',
        status: 'completed',
        progress: 100,
        totalBooks: 200,
        checkedBooks: 200,
        discrepancyCount: 0,
        notes: 'Tất cả sách mới đều đúng số lượng và chất lượng'
      }
    ];

    setChecks(mockChecks);
    
    // Calculate stats
    const total = mockChecks.length;
    const pending = mockChecks.filter(check => check.status === 'pending').length;
    const completed = mockChecks.filter(check => check.status === 'completed').length;
    const discrepancy = mockChecks.filter(check => check.status === 'discrepancy').length;
    
    setStats({ total, pending, completed, discrepancy });
    setLoading(false);
  }, []);

  const getStatusBadge = (status) => {
    switch (status) {
      case 'pending':
        return <span className="badge badge-pending">Đang tiến hành</span>;
      case 'completed':
        return <span className="badge badge-completed">Hoàn thành</span>;
      case 'discrepancy':
        return <span className="badge badge-discrepancy">Có vấn đề</span>;
      default:
        return <span className="badge badge-pending">Không xác định</span>;
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'pending':
        return <FaClipboardCheck />;
      case 'completed':
        return <FaCheckCircle />;
      case 'discrepancy':
        return <FaExclamationTriangle />;
      default:
        return <FaClipboardCheck />;
    }
  };

  const handleNewCheck = () => {
    alert('Chức năng tạo kiểm kê mới sẽ được phát triển');
  };

  const handleViewCheck = (check) => {
    alert(`Xem chi tiết kiểm kê: ${check.title}`);
  };

  const handleEditCheck = (check) => {
    alert(`Chỉnh sửa kiểm kê: ${check.title}`);
  };

  const handleDeleteCheck = (check) => {
    if (window.confirm(`Bạn có chắc chắn muốn xóa kiểm kê "${check.title}"?`)) {
      setChecks(checks.filter(c => c.id !== check.id));
      // Recalculate stats
      const newChecks = checks.filter(c => c.id !== check.id);
      const total = newChecks.length;
      const pending = newChecks.filter(c => c.status === 'pending').length;
      const completed = newChecks.filter(c => c.status === 'completed').length;
      const discrepancy = newChecks.filter(c => c.status === 'discrepancy').length;
      setStats({ total, pending, completed, discrepancy });
    }
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="inventory-checks">
      <div className="page-header">
        <h1 className="page-title">Kiểm kê kho sách</h1>
        <p className="page-subtitle">Quản lý các phiếu kiểm kê và đối chiếu thực tế</p>
      </div>

      <div className="content-section">
        {/* Statistics */}
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-header">
              <div>
                <div className="stat-value">{stats.total}</div>
                <div className="stat-label">Tổng kiểm kê</div>
              </div>
              <div className="stat-icon total">
                <FaClipboardCheck />
              </div>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-header">
              <div>
                <div className="stat-value">{stats.pending}</div>
                <div className="stat-label">Đang tiến hành</div>
              </div>
              <div className="stat-icon pending">
                <FaClipboardCheck />
              </div>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-header">
              <div>
                <div className="stat-value">{stats.completed}</div>
                <div className="stat-label">Hoàn thành</div>
              </div>
              <div className="stat-icon completed">
                <FaCheckCircle />
              </div>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-header">
              <div>
                <div className="stat-value">{stats.discrepancy}</div>
                <div className="stat-label">Có vấn đề</div>
              </div>
              <div className="stat-icon discrepancy">
                <FaExclamationTriangle />
              </div>
            </div>
          </div>
        </div>

        {/* Checks Section */}
        <div className="checks-section">
          <div className="section-header">
            <h2 className="section-title">
              <FaClipboardCheck />
              Danh sách kiểm kê
            </h2>
            <button className="btn btn-primary" onClick={handleNewCheck}>
              <FaPlus /> Tạo kiểm kê mới
            </button>
          </div>

          <div className="table-container">
            <table className="table">
              <thead>
                <tr>
                  <th>Mã kiểm kê</th>
                  <th>Tên kiểm kê</th>
                  <th>Người thực hiện</th>
                  <th>Thời gian</th>
                  <th>Tiến độ</th>
                  <th>Trạng thái</th>
                  <th>Vấn đề</th>
                  <th>Thao tác</th>
                </tr>
              </thead>
              <tbody>
                {checks.map((check) => (
                  <tr key={check.id}>
                    <td>
                      <strong>{check.checkNumber}</strong>
                    </td>
                    <td>
                      <div>
                        <strong>{check.title}</strong>
                        <div style={{ fontSize: '0.8rem', color: '#64748b', marginTop: '4px' }}>
                          {check.notes}
                        </div>
                      </div>
                    </td>
                    <td>
                      <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                        <FaUser style={{ color: '#64748b' }} />
                        {check.assignedTo}
                      </div>
                    </td>
                    <td>
                      <div style={{ fontSize: '0.9rem' }}>
                        <div>Từ: {new Date(check.startDate).toLocaleDateString('vi-VN')}</div>
                        <div>Đến: {new Date(check.endDate).toLocaleDateString('vi-VN')}</div>
                      </div>
                    </td>
                    <td>
                      <div>
                        <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '0.9rem', marginBottom: '5px' }}>
                          <span>{check.checkedBooks}/{check.totalBooks}</span>
                          <span>{check.progress}%</span>
                        </div>
                        <div className="progress-bar">
                          <div 
                            className="progress-fill" 
                            style={{ width: `${check.progress}%` }}
                          ></div>
                        </div>
                      </div>
                    </td>
                    <td>{getStatusBadge(check.status)}</td>
                    <td>
                      {check.discrepancyCount > 0 ? (
                        <span style={{ color: '#dc2626', fontWeight: '600' }}>
                          {check.discrepancyCount} vấn đề
                        </span>
                      ) : (
                        <span style={{ color: '#10b981' }}>Không có</span>
                      )}
                    </td>
                    <td>
                      <div className="action-buttons">
                        <button
                          className="btn btn-primary btn-sm"
                          onClick={() => handleViewCheck(check)}
                          title="Xem chi tiết"
                        >
                          <FaEye />
                        </button>
                        <button
                          className="btn btn-warning btn-sm"
                          onClick={() => handleEditCheck(check)}
                          title="Chỉnh sửa"
                        >
                          <FaEdit />
                        </button>
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={() => handleDeleteCheck(check)}
                          title="Xóa"
                        >
                          <FaTrash />
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {checks.length === 0 && (
            <div className="empty-state">
              <h3>Chưa có kiểm kê nào</h3>
              <p>Bắt đầu tạo kiểm kê mới để quản lý kho sách hiệu quả.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default InventoryChecks;
