import React, { useState, useEffect } from 'react';
import { FaPlus, FaSearch, FaEye, FaCheck, FaTimes, FaFileAlt, FaTruck, FaBoxes, FaCalendarAlt } from 'react-icons/fa';
import './BookImports.css';

const BookImports = () => {
  const [imports, setImports] = useState([]);
  const [filteredImports, setFilteredImports] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [selectedImport, setSelectedImport] = useState(null);
  const [showImportModal, setShowImportModal] = useState(false);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [newImport, setNewImport] = useState({
    supplier: '',
    expectedDate: '',
    totalAmount: 0,
    notes: ''
  });

  // Mock data
  useEffect(() => {
    const mockImports = [
      {
        id: 1,
        importId: 'IMP001',
        supplier: 'Nhà xuất bản Giáo dục',
        supplierContact: '0123456789',
        expectedDate: '2024-02-10',
        actualDate: '2024-02-10',
        totalAmount: 5000000,
        status: 'Đã nhập',
        notes: 'Sách giáo khoa và tài liệu tham khảo',
        items: [
          { bookTitle: 'Lập trình Python', quantity: 50, unitPrice: 80000, total: 4000000 },
          { bookTitle: 'Cơ sở dữ liệu', quantity: 30, unitPrice: 120000, total: 3600000 },
          { bookTitle: 'Mạng máy tính', quantity: 25, unitPrice: 150000, total: 3750000 }
        ]
      },
      {
        id: 2,
        importId: 'IMP002',
        supplier: 'Công ty Sách Alpha',
        supplierContact: '0987654321',
        expectedDate: '2024-02-15',
        actualDate: null,
        totalAmount: 3000000,
        status: 'Chờ nhập',
        notes: 'Sách văn học và tiểu thuyết',
        items: [
          { bookTitle: 'Nhà giả kim', quantity: 20, unitPrice: 120000, total: 2400000 },
          { bookTitle: 'Đắc nhân tâm', quantity: 15, unitPrice: 100000, total: 1500000 }
        ]
      },
      {
        id: 3,
        importId: 'IMP003',
        supplier: 'Nhà xuất bản Trẻ',
        supplierContact: '0555666777',
        expectedDate: '2024-02-08',
        actualDate: '2024-02-08',
        totalAmount: 2000000,
        status: 'Đã nhập',
        notes: 'Sách thiếu nhi và truyện tranh',
        items: [
          { bookTitle: 'Doraemon', quantity: 100, unitPrice: 15000, total: 1500000 },
          { bookTitle: 'Conan', quantity: 50, unitPrice: 25000, total: 1250000 }
        ]
      }
    ];
    setImports(mockImports);
    setFilteredImports(mockImports);
  }, []);

  useEffect(() => {
    let filtered = imports;

    if (searchTerm) {
      filtered = filtered.filter(imp =>
        imp.importId.toLowerCase().includes(searchTerm.toLowerCase()) ||
        imp.supplier.toLowerCase().includes(searchTerm.toLowerCase()) ||
        imp.status.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (statusFilter !== 'all') {
      filtered = filtered.filter(imp => imp.status === statusFilter);
    }

    setFilteredImports(filtered);
  }, [searchTerm, statusFilter, imports]);

  const handleCreateImport = () => {
    setShowImportModal(true);
    setNewImport({
      supplier: '',
      expectedDate: '',
      totalAmount: 0,
      notes: ''
    });
  };

  const confirmCreateImport = () => {
    if (newImport.supplier && newImport.expectedDate) {
      const importData = {
        id: imports.length + 1,
        importId: `IMP${String(imports.length + 1).padStart(3, '0')}`,
        supplier: newImport.supplier,
        supplierContact: 'Chưa có',
        expectedDate: newImport.expectedDate,
        actualDate: null,
        totalAmount: newImport.totalAmount,
        status: 'Chờ nhập',
        notes: newImport.notes,
        items: []
      };
      setImports([...imports, importData]);
      setShowImportModal(false);
      setNewImport({
        supplier: '',
        expectedDate: '',
        totalAmount: 0,
        notes: ''
      });
    }
  };

  const handleReceiveImport = (importData) => {
    const updatedImports = imports.map(imp =>
      imp.id === importData.id
        ? { ...imp, status: 'Đã nhập', actualDate: new Date().toISOString().split('T')[0] }
        : imp
    );
    setImports(updatedImports);
  };

  const getStatusBadge = (status) => {
    const statusClasses = {
      'Chờ nhập': 'status-pending',
      'Đã nhập': 'status-completed',
      'Đã hủy': 'status-cancelled'
    };
    return <span className={`status-badge ${statusClasses[status]}`}>{status}</span>;
  };

  const getTotalItems = (items) => {
    return items.reduce((total, item) => total + item.quantity, 0);
  };

  const getTotalValue = () => {
    return filteredImports.reduce((total, imp) => total + imp.totalAmount, 0);
  };

  const getPendingImports = () => {
    return filteredImports.filter(imp => imp.status === 'Chờ nhập').length;
  };

  return (
    <div className="book-imports">
      <div className="imports-header">
        <h1>Quản Lý Nhập Sách</h1>
        <div className="imports-stats">
          <div className="stat-card">
            <div className="stat-value">{filteredImports.length}</div>
            <div className="stat-label">Tổng phiếu nhập</div>
          </div>
          <div className="stat-card">
            <div className="stat-value">{getPendingImports()}</div>
            <div className="stat-label">Chờ nhập</div>
          </div>
          <div className="stat-card">
            <div className="stat-value">{getTotalValue().toLocaleString()}đ</div>
            <div className="stat-label">Tổng giá trị</div>
          </div>
        </div>
      </div>

      <div className="imports-controls">
        <div className="search-section">
          <div className="search-box">
            <FaSearch className="search-icon" />
            <input
              type="text"
              placeholder="Tìm kiếm theo mã phiếu, nhà cung cấp..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>

        <div className="filter-section">
          <select
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
            className="status-filter"
          >
            <option value="all">Tất cả trạng thái</option>
            <option value="Chờ nhập">Chờ nhập</option>
            <option value="Đã nhập">Đã nhập</option>
            <option value="Đã hủy">Đã hủy</option>
          </select>
        </div>

        <div className="action-section">
          <button className="btn-create" onClick={handleCreateImport}>
            <FaPlus /> Tạo phiếu nhập
          </button>
        </div>
      </div>

      <div className="imports-table-container">
        <table className="imports-table">
          <thead>
            <tr>
              <th>Mã phiếu</th>
              <th>Nhà cung cấp</th>
              <th>Ngày dự kiến</th>
              <th>Ngày thực tế</th>
              <th>Số lượng sách</th>
              <th>Tổng tiền</th>
              <th>Trạng thái</th>
              <th>Thao tác</th>
            </tr>
          </thead>
          <tbody>
            {filteredImports.map((importData) => (
              <tr key={importData.id}>
                <td>{importData.importId}</td>
                <td>
                  <div className="supplier-info">
                    <div className="supplier-name">{importData.supplier}</div>
                    <div className="supplier-contact">{importData.supplierContact}</div>
                  </div>
                </td>
                <td>{new Date(importData.expectedDate).toLocaleDateString('vi-VN')}</td>
                <td>
                  {importData.actualDate 
                    ? new Date(importData.actualDate).toLocaleDateString('vi-VN')
                    : '-'
                  }
                </td>
                <td>{getTotalItems(importData.items)} cuốn</td>
                <td>{importData.totalAmount.toLocaleString()}đ</td>
                <td>{getStatusBadge(importData.status)}</td>
                <td>
                  <div className="action-buttons">
                    <button
                      className="btn-view"
                      onClick={() => {
                        setSelectedImport(importData);
                        setShowDetailModal(true);
                      }}
                      title="Xem chi tiết"
                    >
                      <FaEye />
                    </button>
                    {importData.status === 'Chờ nhập' && (
                      <button
                        className="btn-receive"
                        onClick={() => handleReceiveImport(importData)}
                        title="Xác nhận nhập"
                      >
                        <FaCheck />
                      </button>
                    )}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Create Import Modal */}
      {showImportModal && (
        <div className="modal-overlay">
          <div className="import-modal">
            <div className="modal-header">
              <h3>Tạo Phiếu Nhập Sách</h3>
              <button
                className="close-btn"
                onClick={() => setShowImportModal(false)}
              >
                ×
              </button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label>Nhà cung cấp:</label>
                <input
                  type="text"
                  value={newImport.supplier}
                  onChange={(e) => setNewImport({...newImport, supplier: e.target.value})}
                  placeholder="Nhập tên nhà cung cấp"
                  className="form-input"
                />
              </div>
              <div className="form-group">
                <label>Ngày dự kiến nhập:</label>
                <input
                  type="date"
                  value={newImport.expectedDate}
                  onChange={(e) => setNewImport({...newImport, expectedDate: e.target.value})}
                  className="form-input"
                />
              </div>
              <div className="form-group">
                <label>Tổng giá trị (VNĐ):</label>
                <input
                  type="number"
                  value={newImport.totalAmount}
                  onChange={(e) => setNewImport({...newImport, totalAmount: Number(e.target.value)})}
                  placeholder="Nhập tổng giá trị"
                  className="form-input"
                />
              </div>
              <div className="form-group">
                <label>Ghi chú:</label>
                <textarea
                  value={newImport.notes}
                  onChange={(e) => setNewImport({...newImport, notes: e.target.value})}
                  placeholder="Ghi chú về phiếu nhập..."
                  className="form-textarea"
                  rows="3"
                />
              </div>
            </div>
            <div className="modal-footer">
              <button
                className="btn-cancel"
                onClick={() => setShowImportModal(false)}
              >
                <FaTimes /> Hủy
              </button>
              <button
                className="btn-confirm"
                onClick={confirmCreateImport}
                disabled={!newImport.supplier || !newImport.expectedDate}
              >
                <FaCheck /> Tạo phiếu
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Import Detail Modal */}
      {showDetailModal && selectedImport && (
        <div className="modal-overlay">
          <div className="detail-modal">
            <div className="modal-header">
              <h3>Chi Tiết Phiếu Nhập</h3>
              <button
                className="close-btn"
                onClick={() => setShowDetailModal(false)}
              >
                ×
              </button>
            </div>
            <div className="modal-body">
              <div className="detail-grid">
                <div className="detail-section">
                  <h4>Thông tin phiếu</h4>
                  <div className="detail-item">
                    <label>Mã phiếu:</label>
                    <span>{selectedImport.importId}</span>
                  </div>
                  <div className="detail-item">
                    <label>Nhà cung cấp:</label>
                    <span>{selectedImport.supplier}</span>
                  </div>
                  <div className="detail-item">
                    <label>Liên hệ:</label>
                    <span>{selectedImport.supplierContact}</span>
                  </div>
                  <div className="detail-item">
                    <label>Trạng thái:</label>
                    <span>{getStatusBadge(selectedImport.status)}</span>
                  </div>
                </div>

                <div className="detail-section">
                  <h4>Thông tin thời gian</h4>
                  <div className="detail-item">
                    <label>Ngày dự kiến:</label>
                    <span>{new Date(selectedImport.expectedDate).toLocaleDateString('vi-VN')}</span>
                  </div>
                  <div className="detail-item">
                    <label>Ngày thực tế:</label>
                    <span>
                      {selectedImport.actualDate 
                        ? new Date(selectedImport.actualDate).toLocaleDateString('vi-VN')
                        : 'Chưa nhập'
                      }
                    </span>
                  </div>
                  <div className="detail-item">
                    <label>Tổng giá trị:</label>
                    <span>{selectedImport.totalAmount.toLocaleString()}đ</span>
                  </div>
                </div>

                <div className="detail-section">
                  <h4>Ghi chú</h4>
                  <div className="detail-notes">
                    {selectedImport.notes || 'Không có ghi chú'}
                  </div>
                </div>
              </div>

              <div className="items-section">
                <h4>Danh sách sách nhập</h4>
                <table className="items-table">
                  <thead>
                    <tr>
                      <th>Tên sách</th>
                      <th>Số lượng</th>
                      <th>Đơn giá</th>
                      <th>Thành tiền</th>
                    </tr>
                  </thead>
                  <tbody>
                    {selectedImport.items.map((item, index) => (
                      <tr key={index}>
                        <td>{item.bookTitle}</td>
                        <td>{item.quantity} cuốn</td>
                        <td>{item.unitPrice.toLocaleString()}đ</td>
                        <td>{item.total.toLocaleString()}đ</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
            <div className="modal-footer">
              {selectedImport.status === 'Chờ nhập' && (
                <button
                  className="btn-receive-large"
                  onClick={() => {
                    handleReceiveImport(selectedImport);
                    setShowDetailModal(false);
                  }}
                >
                  <FaCheck /> Xác nhận nhập kho
                </button>
              )}
              <button
                className="btn-print"
                onClick={() => window.print()}
              >
                <FaFileAlt /> In phiếu
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default BookImports;
